INSERT INTO `anime` (`name`, `author`, `description`, `genre`, `view`, `image`, `start_date`) VALUES ('Le bahut des tordus', 'Hiroaki Sakurai', "Régulièrement détruite, la Cromartie High School est un repaire de cancres qui recueille les rebuts de tous les autres établissements. Alors, forcément, quand Takashi, élève studieux, débarque avec son cartable tout neuf et sa trousse bien rangée, il fait un peu tache. Qu'à cela ne tienne, il s'est fixé comme objectif de transformer l'école en un établissement modèle", 'comédie', '2300', 'https://media.senscritique.com/media/000006545394/source_big/Le_bahut_des_tordus.jpg', '2003');
INSERT INTO `anime` (`name`, `author`, `description`, `genre`, `view`, `image`, `start_date`) VALUES ("L'attaque des titans", 'Hajime Isayama', "L’histoire tourne autour du personnage d’Eren Jäger dans un monde où l’humanité vit entourée d’immenses murs pour se protéger de créatures gigantesques, les Titans. Le récit raconte le combat mené par l’humanité pour reconquérir son territoire, en éclaircissant les mystères liés à l’apparition des Titans, du monde extérieur et des évènements précédant la construction des murs.", 'action', '10000', 'https://i1.wp.com/www.filmspourenfants.net/wp-content/uploads/2018/01/lattaque-des-titans-a.jpg?fit=555%2C743&ssl=1', '2009');
INSERT INTO `anime` (`name`, `author`, `description`, `genre`, `view`, `image`, `start_date`) VALUES ('Dororo', 'Osamu Tezuka', "Hyakkimaru, jeune homme dont 48 parties du corps sont offertes à 48 démons au moment de sa naissance. Son père a, en effet, conclu un pacte avec eux afin d'acquérir des pouvoirs. Il abandonne aussitôt son fils qui est élevé par un médecin.", 'action', '5000', 'https://www.nautiljon.com/images/anime/00/67/dororo_7676.jpg', '2019');
INSERT INTO `anime` (`name`, `author`, `description`, `genre`, `view`, `image`, `start_date`) VALUES ('Black Butler', 'Yana Toboso', "Le Comte Ciel Phantomhive est un aristocrate anglais du XIX e siècle au passé tourmenté. Entre les années 1888 et 1889, âgé de 12 ans et ayant perdu ses parents dans un incendie inexplicable, il se voit devenir l'héritier de l'entreprise de jouets et friandises Phantom.", 'action', '2000', 'https://images-na.ssl-images-amazon.com/images/I/814OLNRNsCL.jpg', '2008');
INSERT INTO `anime` (`name`, `author`, `description`, `genre`, `view`, `image`, `start_date`) VALUES ('Osomatsu-san', 'Fujio Akatsuka', "L'anime suit la vie des six garçons de la famille Matsuno, des sextuplés ayant passé leur enfance à faire des bêtises et qui sont maintenant de jeunes adultes", 'comédie', '7000', 'https://encrypted-tbn2.gstatic.com/images?q=tbn:ANd9GcQUSVsMOC0KSDeUJ8hRDqXuydiNxRw4xXKG49GbjOG7Rsacahro', '2015');
INSERT INTO `anime` (`name`, `author`, `description`, `genre`, `view`, `image`, `start_date`) VALUES ('Lucky Star', 'Toka Takei', "Lucky Star s'intéresse aux vies de plusieurs jeunes lycéennes japonaises, donnant une touche d'humour à leurs histoires. L'héroïne principale est Konata Izumi, une jeune fille sportive et intelligente qui, malgré ces qualités, ne fait pas partie d'un club sportif et a de mauvaises notes.", 'comédie', '3000', 'https://derf9v1xhwwx1.cloudfront.net/image/upload/oth/FunimationStoreFront/1422969/Japanese/1422969_Japanese_ContinueWatchingMobile_f5e26b4d-20ba-4f8b-8b6d-97fa26fc48ef.jpg', '2007');
